import React, { Component } from 'react';

const BarreDore = ({ color, width, height, position, opacity, top, right }) => (
  <div
    style={{
      backgroundColor: color,
      height: height,
      width: width,
      opacity: opacity,
      position: position,
      top: top,
      right: right
    }}
  ></div>
);

const Titre = ({
  value,
  color,
  bgcolor,
  opacity,
  fontSize,
  width,
  height,
  position,
  left,
  top
}) => (
  <div
    className='p-1 d-flex justify-content-center align-items-center'
    style={{
      value: value,
      color: color,
      backgroundColor: bgcolor,
      opacity: opacity,
      fontSize: fontSize,
      width: width,
      height: height,
      position: position,
      left: left,
      top: top
    }}
  >
    Top Barber Shop in &nbsp;
    <span style={{ color: '#A89331' }}> Charleroi</span>
  </div>
);

const Image = ({ position, width, height, top, left }) => (
  <img
    style={{
      position: position,
      width: width,
      height: height,
      top: top,
      left: left
    }}
    src={require('../../img/6.jpg')}
    alt='Homeimg'
  />
);

const Text = ({ position, left, top, fontSize, color }) => (
  <p
    style={{
      position: position,
      top: top,
      left: left,
      fontSize: fontSize,
      color: color
    }}
  >
    Lorem ipsum dolor sit amet, consectetur <br /> adipiscing elit, sed do
    eiusmod tempor <br /> incididunt ut labore et dolore magna <br />
    aliqua. Quam adipiscing vitae proin sagittis <br /> nisl. Pretium lectus
    quam id leo in vitae. <br /> Amet mattis vulputate enim nulla aliquet <br />{' '}
    porttitor lacus luctus. Velit ut tortor <br />
    pretium viverra suspendisse. Lacus <br />
    vestibulum sed arcu non odio euismod. <br /> Velit ut tortor pretium viverra
    suspendisse. <br /> Lacus vestibulum sed arcu non odio <br />
    euismod.
  </p>
);

const Button = ({ position, top, left, bgcolor }) => (
  <button
    style={{
      position: position,
      top: top,
      left: left,
      backgroundColor: bgcolor
    }}
  >
    Contact us
  </button>
);

export class Home extends Component {
  render() {
    return (
      <div>
        <BarreDore
          color='#5A4D10'
          height='295px'
          width='32px'
          opacity='0.2'
          position='absolute'
          top='0'
        />
        <div className='d-flex justify-content-around pt-5'>
          <div>
            <Titre
              color='white'
              bgcolor='rgba(0,0,0,0.5)'
              width='1020px'
              height='93px'
              fontSize='65pt'
              position='absolute'
              left='120px'
              top='250px'
            />

            <Text
              fontSize='15pt'
              color='white'
              position='absolute'
              top='400px'
            />

            <Button bgcolor='#5A4D10' position='absolute' top='800px' />
          </div>
          <Image position='relatif' width='750px' height='639px' />
        </div>
        <BarreDore
          color='#5A4D10'
          height='295px'
          width='32px'
          opacity='0.2'
          position='absolute'
          top='736px'
          right='0'
        />
      </div>
    );
  }
}

export default Home;
