import React, { Component } from 'react';

const Titre = ({ bgcolor, color, width, height }) => (
  <h1
    className='p-2 ml-5 pl-3'
    style={{
      backgroundColor: bgcolor,
      color: color,
      width: width,
      height: height
    }}
  >
    Photos
  </h1>
);

const Photo1 = ({
  position,
  width,
  height,
  top,
  left,
  marginLeft,
  require
}) => (
  <a href='https://www.instagram.com/p/B6F82NSlKSH/'>
    <img
      style={{
        position: position,
        width: width,
        height: height,
        top: top,
        left: left,
        marginLeft: marginLeft
      }}
      src={{ require }}
      alt='galeriePhoto1'
    />
  </a>
);

export class Photos extends Component {
  render() {
    return (
      <div style={{ marginTop: '100px' }}>
        <Titre bgcolor='rgba(255,255,255,0.1)' color='white' width='400px' />
        <div>
          <Photo1
            width='450px'
            height='365px'
            position='relative'
            left='48px'
            top='30px'
            require='../../img/12.jpg'
          />
        </div>
      </div>
    );
  }
}

export default Photos;
