import React, { Component } from 'react';

const ImageTarifs = ({ position, width, height, top, left }) => (
  <img
    style={{
      width: width,
      height: height
    }}
    src={require('../../img/tarifs.png')}
    alt='Homeimg'
  />
);

const Titre = ({ color, bgColor, width, fontSize }) => (
  <h1
    className='d-flex justify-content-center'
    style={{
      color: color,
      backgroundColor: bgColor,
      width: width,
      fontSize: fontSize
    }}
  >
    Services we offer
  </h1>
);

const ServiceRendu = ({ color, fontSize }) => (
  <p
    style={{
      color: color,
      fontSize: fontSize
    }}
  >
    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod
    tempor incididunt ut labore et dolore magna aliqua. Quam adipiscing vitae
    proin sagittis nisl. Pretium lectus quam id leo in vitae. Amet mattis
    vulputate enim nulla aliquet porttitor lacus luctus. Velit ut tortor pretium
    viverra suspendisse. Lacus vestibulum sed arcu non odio euismod. Velit ut
    tortor pretium viverra suspendisse. Lacus vestibulum sed arcu non odio
    euismod.
  </p>
);

export class Services extends Component {
  constructor() {
    super();
    this.state = {
      serviceBgColor: 'rgba(90,77,16,0.25)',
      priceBgColor: 'black',
      promotionBgColor: 'black'
    };
  }

  handleClick(t) {
    console.log(t);
    if (t === 1) {
      this.setState({
        serviceBgColor: 'rgba(90,77,16,0.25)',
        priceBgColor: 'black',
        promotionBgColor: 'Black'
      });
    } else if (t === 2) {
      this.setState({
        serviceBgColor: 'Black',
        priceBgColor: 'rgba(90,77,16,0.25)',
        promotionBgColor: 'Black'
      });
    } else if (t === 3) {
      this.setState({
        serviceBgColor: 'Black',
        priceBgColor: 'black',
        promotionBgColor: 'rgba(90,77,16,0.25)'
      });
    }
  }

  render() {
    return (
      <div style={{ marginTop: '200px' }}>
        <div style={{ marginLeft: '459.99px' }}>
          <button
            className='mx-3'
            onClick={() => this.handleClick(1)}
            style={{
              backgroundColor: this.state.serviceBgColor,
              width: '200px',
              outline: 'none'
            }}
          >
            Services
          </button>

          <button
            className='mx-3'
            onClick={() => this.handleClick(2)}
            style={{
              backgroundColor: this.state.priceBgColor,
              width: '200px',
              outline: 'none'
            }}
          >
            Prix
          </button>

          <button
            className='mx-3'
            onClick={() => this.handleClick(3)}
            style={{
              backgroundColor: this.state.promotionBgColor,
              width: '200px',
              outline: 'none'
            }}
          >
            Promotions
          </button>
        </div>
        <div className='d-flex '>
          <ImageTarifs />
          <div
            className='container p-5'
            style={{
              height: '492.99px',
              backgroundColor: 'rgba(90,77,16,0.25)'
            }}
          >
            <Titre
              color='white'
              bgColor='rgba(0,0,0,0.5)'
              fontSize='54pt'
              width='500px'
            />
            <ServiceRendu color='white' fontSize='15pt' />
          </div>
        </div>
      </div>
    );
  }
}

export default Services;
