import React, { Component } from 'react';

const ServiceBtn = ({
  position,
  top,
  left,
  bgcolor,
  width,
  height,
  onClick
}) => (
  <button
    className='mx-3'
    onClick={onClick}
    style={{
      position: position,
      top: top,
      left: left,
      backgroundColor: bgcolor,
      width: width,
      height: height
    }}
  >
    Services
  </button>
);

const PrixBtn = ({ position, top, left, bgcolor, width, height, onClick }) => (
  <button
    className='mx-3'
    onClick={onClick}
    style={{
      position: position,
      top: top,
      left: left,
      backgroundColor: bgcolor,
      width: width,
      height: height
    }}
  >
    Prix
  </button>
);

const PromotionBtn = ({
  position,
  top,
  left,
  bgcolor,
  width,
  height,
  onClick
}) => (
  <button
    className='mx-3'
    onClick={onClick}
    style={{
      position: position,
      top: top,
      left: left,
      backgroundColor: bgcolor,
      width: width,
      height: height
    }}
  >
    Promotions
  </button>
);

const ImageTarifs = ({ position, width, height, top, left }) => (
  <img
    style={{
      position: position,
      width: width,
      height: height,
      top: top,
      left: left
    }}
    src={require('../../img/tarifs.png')}
    alt='Homeimg'
  />
);

const Titre = ({
  color,
  bgColor,
  position,
  width,
  height,
  top,
  left,
  fontSize
}) => (
  <h1
    className='d-flex justify-content-center'
    style={{
      color: color,
      backgroundColor: bgColor,
      position: position,
      width: width,
      height: height,
      top: top,
      left: left,
      fontSize: fontSize
    }}
  >
    Services we offer
  </h1>
);

const ServiceRendu = ({
  position,
  width,
  height,
  top,
  left,
  color,
  fontSize
}) => (
  <p
    style={{
      position: position,
      width: width,
      height: height,
      top: top,
      left: left,
      color: color,
      fontSize: fontSize
    }}
  >
    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod
    tempor incididunt ut labore et dolore magna aliqua. Quam adipiscing vitae
    proin sagittis nisl. Pretium lectus quam id leo in vitae. Amet mattis
    vulputate enim nulla aliquet porttitor lacus luctus. Velit ut tortor pretium
    viverra suspendisse. Lacus vestibulum sed arcu non odio euismod. Velit ut
    tortor pretium viverra suspendisse. Lacus vestibulum sed arcu non odio
    euismod.
  </p>
);

export class Services extends Component {
  constructor() {
    super();
    this.state = {
      serviceBgColor: 'rgba(90,77,16,0.25)',
      priceBgColor: 'black',
      promotionBgColor: 'black'
    };
  }

  handleClick() {
    if (this.state.serviceBgColor === 'black') {
      this.setState({
        serviceBgColor: 'rgba(90,77,16,0.25)',
        priceBgColor: 'black',
        promotionBgColor: 'Black'
      });
    } else if (priceBgColor === 'black') {
      this.setState({
        serviceBgColor: 'black',
        priceBgColor: 'rgba(90,77,16,0.25)',
        promotionBgColor: 'Black'
      });
    } else {
      this.setState({
        serviceBgColor: 'black',
        priceBgColor: 'black',
        promotionBgColor: 'rgba(90,77,16,0.25)'
      });
    }
  }
  render() {
    return (
      <div style={{ marginTop: '200px' }}>
        <div style={{ marginLeft: '459.99px' }}>
          <ServiceBtn
            bgcolor={this.state.serviceBgColor}
            width='200px'
            onClick={() => this.handleClick()}
          />
          <PrixBtn
            bgcolor={this.state.priceBgColor}
            width='200px'
            onClick={() => this.handleClick()}
          />
          <PromotionBtn
            bgcolor={this.state.promotionBgColor}
            width='200px'
            onClick={() => this.handleClick()}
          />
        </div>
        <div className='d-flex '>
          <ImageTarifs />
          <div
            className='container p-5'
            style={{
              height: '492.99px',
              backgroundColor: 'rgba(90,77,16,0.25)'
            }}
          >
            <Titre
              color='white'
              bgColor='rgba(0,0,0,0.5)'
              fontSize='54pt'
              width='500px'
            />
            <ServiceRendu color='white' fontSize='15pt' />
          </div>
        </div>
      </div>
    );
  }
}

export default Services;
